            <v-stepper-content step="2">
              <p class="welcome__confirm-phone-title">
                Проверьте <br />
                номер телефона
              </p>

              <p class="welcome__confirm-phone">{{ phone }}</p>
              <p class="welcome__confirm-phone-desc">На этот номер придёт код проверки</p>

              <login-button @click="e1 = 3" label="Выслать код" />

              <p class="welcome__stepper-back">
                <a class="welcome__stepper-back-link" @click="e1 = 1"> Вернуться назад </a>
              </p>
            </v-stepper-content>

            <v-stepper-content step="3">
              <p class="welcome__confirm-phone-title welcome__confirm-phone-title--no-margin">Введите код</p>

              <p class="welcome__confirm-phone welcome__confirm-phone--small">Код выслан на номер {{ phone }}</p>

              <v-otp-input plain type="number" class="welcome__otp" length="4"></v-otp-input>

              <login-button label="Подтвердить номер" />

              <p class="welcome__stepper-back">
                <a class="welcome__stepper-back-link" @click="e1 = 2"> Вернуться назад </a>
              </p>
            </v-stepper-content>
          </v-stepper-items>
        </v-stepper>
      </div>
    </div>
    <div class="welcome__presentation">
      <v-carousel :show-arrows="false" cycle interval="6000" height="auto" hide-delimiter-background>
        <v-carousel-item v-for="(slider, index) in sliders" :key="index">
          <img :src="slider.img" :alt="slider.text" />
          <p class="h2 text-center">{{ slider.text }}</p>
        </v-carousel-item>
      </v-carousel>
    </div>
  </div>
</template>

<script>
import { required, email, minLength, maxLength, sameAs } from 'vuelidate/lib/validators'
import { AuthApi } from '@/store/api'

import LoginButton from '@/components/library/buttons/LoginButton.vue'
import BaseRadioButton from '../components/library/BaseRadioButton.vue'

export default {
  name: 'Registration',
  data() {
    return {
      e1: 1,
      phone: '',
      user_type: 'Тренер',
      full_name: '',
      confirmPassword: '',
      allowPersonalDataProcessing: false,
      rejectText: '',
      sliders: [
        {
          img: require('@/assets/images/login/login-slide-1.png'),
          text: 'Наблюдайте динамику показателей команды в реальном времени. Наглядно сравнивайте показатели и  меняйте программу тренировки максимально эффективно',
        },
        {
          img: require('@/assets/images/login/login-slide-2.png'),
          text: 'При выборе программы тренировки, обращайте внамание на интенсивность занятий в течение недели ',
        },
      ],
    }
  },
  components: { LoginButton, BaseRadioButton },
  validations: {
    firstName: {
      required,
      minLength: minLength(2),
      maxLength: maxLength(50),
    },
    lastName: {
      required,
      minLength: minLength(2),
      maxLength: maxLength(50),
    },
    middleName: {
      maxLength: maxLength(50),
    },
    email: {
      required,
      email,
      minLength: minLength(7),
      maxLength: maxLength(64),
    },
    password: {
      required,
      minLength: minLength(8),
      maxLength: maxLength(50),
    },
    confirmPassword: {
      required,
      sameAsPassword: sameAs('password'),
      maxLength: maxLength(50),
    },
    allowPersonalDataProcessing: {
      checked(val) {
        return val
      },
    },
  },
  methods: {
    submit() {
      this.$v.$touch()

      AuthApi.registration({
        email: this.email,
        password: this.password,
        password_confirmation: this.confirmPassword,
      }).then(
        response => {
          this.$store.dispatch('auth/login', response.data.data.user.authentication_token)
          this.$router.push('/')
        },
        reason => {
          console.log('error')
          console.log(reason)
          this.rejectText = reason.status + ' ' + reason.statusText
        }
      )
      console.log(this.email)
    },
  },
  computed: {
    firstNameErrors() {
      const errors = []
      if (!this.$v.firstName.$dirty) return errors
      !this.$v.firstName.minLength && errors.push('Имя должно содержать минимум 2 символа')
      !this.$v.firstName.maxLength && errors.push('Имя должно содержать максимум 50 символа')
      !this.$v.firstName.required && errors.push('Имя обязательно')
      return errors
    },
    middleNameErrors() {
      const errors = []
      if (!this.$v.middleName.$dirty) return errors
      !this.$v.middleName.maxLength && errors.push('Отчество должно содержать максимум 50 символа')
      return errors
    },
    lastNameErrors() {
      const errors = []
      if (!this.$v.lastName.$dirty) return errors
      !this.$v.lastName.minLength && errors.push('Фамилия должна содержать минимум 2 символа')
      !this.$v.lastName.maxLength && errors.push('Фамилия должна содержать максимум 50 символа')
      !this.$v.lastName.required && errors.push('Фамилия')
      return errors
    },
    emailErrors() {
      const errors = []
      if (!this.$v.email.$dirty) return errors
      !this.$v.email.email &&
        errors.push('Проверьте правильность написания почты. Адрес почта содержит символ @, например:ivanov@pochta.ru')
      !this.$v.email.required && errors.push('Укажите почту')
      return errors
    },
    passwordErrors() {
      const errors = []
      if (!this.$v.password.$dirty) return errors
      !this.$v.password.minLength && errors.push('Пароль должен содержать минимум 8 символов')
      !this.$v.password.maxLength && errors.push('Пароль должен содержать максимум 50 символов')
      !this.$v.password.required && errors.push('Пароль обязателен')
      return errors
    },
    confirmPasswordErrors() {
      const errors = []
      if (!this.$v.confirmPassword.$dirty) return errors
      !this.$v.confirmPassword.sameAsPassword && errors.push('Пароли должны совпадать')
      !this.$v.confirmPassword.maxLength && errors.push('Пароль должен содержать максимум 50 символов')
      !this.$v.confirmPassword.required && errors.push('Подтвердите пароль')
      return errors
    },
    allowPersonalDataProcessingErrors() {
      const errors = []
      if (!this.$v.allowPersonalDataProcessing.$dirty) return errors
      !this.$v.allowPersonalDataProcessing.checked && errors.push('Вы должны согласиться!')
      return errors
    },
  },
}
</script>

